javascript:(()=>{var e=location.href,t=document.title,r=`<a href="${e}">${t=(t=t.replace(/^\d+\.\s+/,"")).replace(/^\(\d+\)\s+/,"")}</a>`;navigator.clipboard.write([new ClipboardItem({"text/html":new Blob([r],{type:"text/html"}),"text/plain":new Blob([r],{type:"text/plain"})}),]).then(()=>{alert("success")},()=>{alert("error")})})();